&lt;figure&gt;
	&lt;img src=&quot;picture.jpg&quot; alt=&quot;An awesome picture&quot;&gt;
&lt;/figure&gt;

&lt;!-- Figure with figcaption --&gt;
&lt;figure&gt;
	&lt;img src=&quot;picture.jpg&quot; alt=&quot;An awesome picture&quot;&gt;

	&lt;figcaption&gt;Caption for the awesome picture&lt;/figcaption&gt;
&lt;/figure&gt;

