   &lt;track kind=&quot;subtitles&quot; src=&quot;sampleSubtitles_oz.srt&quot; srclang=&quot;oz&quot;&gt;&lt;/track&gt;
   &lt;track kind=&quot;metadata&quot; src=&quot;keyStage1.srt&quot; srclang=&quot;en&quot; label=&quot;Key Stage 1&quot;&gt;&lt;/track&gt;
